$(document).ready(function(){$("#messagesList").on("click","li",function(){console.log(this);var n=$(this).find("img").attr("alt");$("#txtmessage").val("@"+n+": ")});tinymce.init({selector:"textarea",plugins:["advcode advlist anchor autolink codesample colorpicker contextmenu image imagetools"," lists link linkchecker media mediaembed noneditable powerpaste preview"," table template textcolor wordcount"]})});$("#btnhostmessage").on("click",function(n){var i,t,r,u;$("#txtuser").val();i=$("#txtuser").val();t=$("#txtmessage").val();t+=tinyMCE.activeEditor.getContent();$("#txtmessage").val("");r=document.getElementById("chatcontent");r.scrollTop+=1e3;u={username:i,message:t};t&&connection.invoke("SendPrivateMessage",u).catch(n=>console.error(n));n.preventDefault()});